import React, {useState, useEffect } from 'react';
import { commerce } from './lib/commerce';
import { Products, Navbar, Cart, Checkout} from './componets';
import { BrowserRouter as Router, Switch, Route} from 'react-router-dom';

const App = () => {
    const [products, setProducts] = useState([]);
    const [cart, setCart] = useState({});
    const [order, setOrder] = useState({});
    const [errorMessege, setErrorMessege] = useState('');

    const fetchProduct = async () => {
        const {data} = await commerce.products.list();

        setProducts(data);
    };
    const fetchCart = async () => {

        setCart(await commerce.cart.retrieve());
    };

    const handleAddToCart =  async (productId, quantity) => {
        const {cart} = await commerce.cart.add(productId, quantity);

        setCart(cart);
    };

    const handleUpdateCartQty =  async (productId, quantity) => {
        const {cart} = await commerce.cart.update(productId, {quantity} );

        setCart(cart);
    };

    const handleRemoveFromCart =  async (productId) => {
        const {cart} = await commerce.cart.remove(productId);

        setCart(cart);
    };

    const handleEmptyCart =  async () => {
        const {cart} = await commerce.cart.empty();

        setCart(cart)
    };

    const refreshCart = async () => {
        const newCart = await commerce.cart.refresh();

        setCart(newCart);
    };
    const handleCaptureCheckout = async (checkoutTokenId, newOrder) => {
        try {
            const incomingOrder = await commerce.checkout.capture(checkoutTokenId, newOrder);
            setOrder(incomingOrder);
            refreshCart();
        } catch (error) {
            setErrorMessege(error.data.error.message);
        }
    };

    useEffect(() => {
        fetchProduct();
        fetchCart();
    }, []);

    // console.log(cart)
    return (
        <Router>
            <div>
                <Navbar totalItems={cart.total_items}/>
                <Switch>
                    <Route exact path="/">
                        <Products products={products} onAddToCart={handleAddToCart}/>
                    </Route>

                    <Route exact path="/cart"> 
                        <Cart cart={cart} 
                        handleUpdateCartQty = {handleUpdateCartQty}
                        handleRemoveFromCart = {handleRemoveFromCart}
                        handleEmptyCart = {handleEmptyCart}
                        style={{ width: "100%",
        height: "100%", backgroundImage: `url(${'https://img.freepik.com/free-vector/stylish-hexagonal-line-pattern-background_1017-19742.jpg?size=626&ext=jpg'})`}}
                        />
                    </Route>

                    <Route exact path="/checkout"> 
                        <Checkout
                            cart={cart}
                            order={order}
                            onCaptureCheckout={handleCaptureCheckout}
                            error={errorMessege}
                        />
                    </Route>
                </Switch>


            </div>
        </Router>
    );
};

export default App
